def sliding_window(iterable, n):
    """Create a sliding window of size n."""
    iterators = itertools.tee(iterable, n)
    for i, it in enumerate(iterators):
        # Advance each iterator by i positions
        for _ in range(i):
            next(it, None)
    return zip(*iterators)

