def roundrobin(*iterables):
    """Take elements from iterables in round-robin fashion."""
    iterators = [iter(it) for it in iterables]
    while iterators:
        for it in iterators[:]:
            try:
                yield next(it)
            except StopIteration:
                iterators.remove(it)

